            #(.INIT(1'b0))
         CntThreeFf
            (.D(IntCntLog[n]), .CE(CntEna), .C(CntClk), .CLR(CntRst), .Q(IntCnt[n]));
    end
  endgenerate
    //
         FDCE 
            #(.INIT(1'b0))
         CntThreeTcFf
            (.D(IntCntTcLog), .CE(CntEna), .C(CntClk), .CLR(CntRst), .Q(IntCntTc));
