// Constants, Wire Declarations
////////////////////////////////////////////////////////////////////////////////////////////-
// Constants
wire Low  = 1'b0;
wire High = 1'b1;
// wires
reg [7:0] IntBitSlipPosition;
reg IntFrstBitSlipPstn;
reg [7:0] IntBitSlipData;
reg [7:0] IntRankTwo;
reg [7:0] IntRankTre;
wire IntEnaReg;
reg IntEnaReg_d;
wire IntShftSlipReg;
reg IntSlipPulse_d;
reg IntShiftEna_d;
wire IntSlipPulse;
wire IntShiftEna;
wire IntShftCntTc;
wire IntShftCntEna;
wire IntCompEqu;
reg IntCompEqu_d;
wire IntCompEqu_Rst;
wire IntShftCntRst;
wire [2:0] IntBitSlipCntOut;
wire IntErrOut;
wire IntErrOut_d;
