    assign IntShiftEna = IntShiftEna_d || IntSlipPulse;
    assign IntShftCntEna = IntShiftEna && Ena_pin;
    assign IntShftSlipReg = IntShiftEna;
    assign IntEnaReg = IntShftCntTc || IntEnaReg_d;

