                             (!is_N_pin[bs] && C_SERIALIZATION_FACTOR == 4 && w_rx_q[bs] == bitslip_val_4b ) ||
                             ( is_N_pin[bs] && C_SERIALIZATION_FACTOR == 4 && w_rx_q[bs] == inv_bitslip_val_4b )) ||
