                             ( is_N_pin[bs] && C_SERIALIZATION_FACTOR == 8 && w_bs_q_int[bs] == inv_bitslip_val) || 
                             (!is_N_pin[bs] && C_SERIALIZATION_FACTOR == 4 && w_bs_q_int[bs] == bitslip_val_4b ) ||
