        assign w_sync_done[bs1] =  (C_ENABLE_BITSLIP == 1'b1 && (C_RX_BS_INST[bs1] == 1'b1) && !C_STROBE_BS[bs1]) ? sync_done_r[bs1] : 1'b1;
     end
   endgenerate
    
   assign rx_bitslip_sync_done = &w_sync_done;

